namespace Plexx {
    import Vec2 = Plexx.Mathlib.Vec2;

    export type MouseEventType = "mouseenter" | "mouseover" | "mousemove" | "mousedown"
        | "mouseup" | "click" | "dbclick" | "contextmenu" | "wheel" | "mouseleave" |
        "mouseout" | "mouseout" | "select" | "pointerlockchange" |
        "pointerlockerror";

    export class MouseEvent {
        public position: Vec2;
        public type: MouseEventType;

        constructor(type: MouseEventType, position: Vec2) {
            this.type = type;
            this.position = position;
        }
    }
}