namespace Plexx {
    import Point2D = Plexx.Mathlib.Vec2;

    export type PlexxEventType = "mousemove" | "mousein" | "mouseout" | "click"
        | "mouseup" | "mousedown" | "dragging" | "draggingend" | "none";

    export class PlexxEvent {
        public position: Point2D;
        public type: PlexxEventType;

        constructor(type: PlexxEventType, position: Point2D) {
            this.type = type;
            this.position = position;
        }
    }
}
