/// <reference path="../helper/references.ts" />

namespace Plexx {

    export abstract class CompositeNode extends SceneGraphNode {

        public render(renderContext: RenderContext, preRenderMatrix: Plexx.Mathlib.Mat3): boolean {
            return false;
        }

        public toSVGString(renderContext: RenderContext): string {
            let xmlComment = new XMLComment(this.getName());
            return xmlComment.getLineCommentTag();
        }
    }
}
