/// <reference path="../../helper/references.ts" />

namespace Plexx {

    export abstract class TransformationNode extends SceneGraphNode {

        private transformationMatrix: Plexx.Mathlib.Mat3;
        private inverseTransformationMatrix: Plexx.Mathlib.Mat3;

        public getTransformationMatrix(): Plexx.Mathlib.Mat3 {
            return this.transformationMatrix;
        }

        public getInverseTransformationMatrix(): Plexx.Mathlib.Mat3 {
            return this.inverseTransformationMatrix;
        }

        public accept(nodeVisitor: Plexx.NodeVisitorInterface): number {
            return nodeVisitor.visitTransformationNode(this);
        }
    }
}
