/// <reference path="../../../node_modules/@types/qunit/index.d.ts" />
/// <reference path="../../../framework/build/plexx.d.ts" />

QUnit.test("xmlTag", function( assert ) {
    let xmlTag = new Plexx.XMLTag("circle");
    assert.ok(xmlTag.getStartTag() === "<circle>", "Passed!");
    assert.ok(xmlTag.getEndTag() === "</circle>", "Passed!");
});
